mapper.select:
select a.bno, a.tno, t.title, a.subtitle, a.content, a.uid, a.uname, a.regdate, a.viewcnt, a.replycnt, a.likecnt, a.dislikecnt from tbl_board a
inner join tbl_title t on t.tno = a.tno
where bno = #{bno}

SELECT a.sno, a.subscribed, a.subscriber, b.uname, IFNULL(p.profileFullName, '/account.png') as profileFullName FROM tbl_subscribe a
inner join tbl_user b on a.subscriber = b.uid
left outer join tbl_profile p on b.uno = p.uno
where a.subscribed = #{uid}
order by a.regdate desc limit #{cri.pageStart}, #{cri.perPageNum}
