mapper.select:
select u.uid, u.upw, u.uname, IFNULL(p.profileFullName, '/account.png') as profileFullName from tbl_user u
left outer join tbl_profile p on u.uno = p.uno
where uid = #{uid} and upw = #{upw}

mapper.update:
update tbl_user set 
subscribedcnt = (select count(*) from tbl_subscribe where subscribed = #{subscribed})
where uid = #{subscribed}

update tbl_user set 
subscribercnt =	(select count(*) from tbl_subscribe where subscriber = #{subscriber})
where uid = #{subscriber}

update tbl_user set uname =#{uname}

, upw = #{upw}

where uno = #{uno}
